    private int anzahlTüren;

    public Auto(int t) { this.anzahlTüren = t; }

    @Override
    public void starten() { System.out.println("Auto startet"); }

    @Override
    public void stoppen() { System.out.println("Auto stoppt"); }

    // Polymorphie: Methode, die nur in Auto existiert
    public void hupen() { System.out.println("Hup Hup"); }
}

// Polymorphie: gleiche Schnittstelle, unterschiedliche Implementierung
class Motorrad implements Fahrzeug {
    @Override
    public void starten() { System.out.println("Motorrad startet"); }

    @Override
    public void stoppen() { System.out.println("Motorrad stoppt"); }
}

public class Main {
    public static void main(String[] args) {
        Fahrzeug f1 = new Auto(4);
        Fahrzeug f2 = new Motorrad();

        f1.starten(); // Auto startet
        f2.starten(); // Motorrad startet

        // Polymorphie: Laufzeitbindung
        if (f1 instanceof Auto) {
            ((Auto) f1).hupen(); // Hup Hup
        }
    }
}
```

